/**
 * Error indicating that no suggestions could be generated by the AI.
 * This should be handled by showing an inline message, not a toast notification.
 */
export class NoSuggestionsError extends Error {
  constructor(message: string = 'Could not generate suggestions') {
    super(message);
    this.name = 'NoSuggestionsError';
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, NoSuggestionsError);
    }
  }
}

export function isNoSuggestionsError(error: unknown): error is NoSuggestionsError {
  return error instanceof NoSuggestionsError;
}
